mapper.select:
SELECT B.BOARD_SEQ, B.TITLE, B.CONTENTS, B.REG_DATE FROM BOARD B
AND B.TITLE LIKE CONCAT('%', #{parameter.keyword}, '%')
AND B.BOARD_TYPE IN (
#{value}
)
ORDER BY B.REG_DATE DESC LIMIT #{pageRequest.limit}, #{pageRequest.offset}

mapper.update:
UPDATE BOARD
SET
	TITLE = #{title},
	CONTENTS = #{contents}
WHERE BOARD_SEQ = #{boardSeq}
